script.source:

#!/bin/bash

# $VAL contains the answer of the user set by autoyast
# in the default case, we do nothing
[ "$VAL" = "false" ] && {
	# this statement tells yast to skip the next question which is the
	# question around the encryption key
	echo "3" > /tmp/next_dialog
	exit
}

#!/bin/bash
LOC="/tmp/cc-package-uri"
$(echo "$VAL" | grep -E -q "^http://") || \ $(echo "$VAL" | grep -E -q "^ftp://") || \ $(echo "$VAL" | grep -E -q "^file:///") && \ { echo "$VAL" > $LOC exit 0 }
echo "FAILED: wrong URI format for package location provided: $VAL" echo "FAILED: only http://, ftp:// and file:/// supported" exit 1
